var express = require('express'); 
var app = express(); 
var marked = require("marked"); 

marked.setOptions({ 
 sanitize: true 
 }); 

app.locals.marked = marked; 

app.get('/', function(req, res){ 
    res.send("<html><body>hello world<script type='text/javascript'>alert('got you')</script></body></html>"); 
}); 

app.listen(3000);

