remediationSample:
-- HTML --
<div id="popup" class="popup hidden">Legacy popup</div>

-- CSS --
.popup {
  position: absolute;
  padding: 8px;
  border: 1px solid #ccc;
  background: #fff;
}
.hidden {
  display: none;
}

-- JS --
function showPopup(x, y) {
  var popup = document.getElementById("popup");
  popup.style.left = x + "px";
  popup.style.top = y + "px";
  popup.classList.remove("hidden");
}

