remediationSample:
// require
var Vue = require('vue')
Vue.use(require('vue-cookies'))

// es2015 module
import Vue from 'vue'
import VueCookies from 'vue-cookies'
Vue.use(VueCookies)

// set global cookie
Vue.$cookies.set('hover-time','1s');

// set local cookie

// name, time, path, domain, secure
this.$cookies.set("token","1s","/path","sub.domain.com",true); // no violation

