remediationSample:
var express = require('express'); 
var app = express(); 

var xssFilter = require('x-xss-protection') 
app.use(xssFilter()) 

// or with helmet 

var helmet = require('helmet'); 
app.use(helmet()) 

// do not disable the xssFilter middleware:
// app.use(helmet({ xssFilter: false})) 

// to activate explicitely the xssFilter middleware 
app.use(helmet.xssFilter()); 

app.listen(3000);

