remediationSample:
function readJSON(filePath, callback) {

  fs.readFile(filePath, (err, data) => {

    var parsedJson;

    // Handle error
    if (err) {
      return callback(err);
    }

    // Parse JSON
    try {
      parsedJson = JSON.parse(data);
    } catch (exception) {
      return callback(exception);
    }

    // Everything is ok
    return callback(null, parsedJson);
  });
}

sample:
function readJSON(filePath, callback) {

  fs.readFile(filePath, (err, data) => {

    var parsedJson;

    // Handle error
    if (err) {
      return callback(err);
    }

    // Parse JSON
    parsedJson = JSON.parse(data);

    return (parsedJson !== 'undefined');
  });
}
