remediationSample:
//Manually disable the X-Powered-By in your express instance or use third-party helmet package: 

import * as express from "express";
var app = express(); 

app.disable('x-powered-by'); 

// or use the hide powered by middleware
import * as hidePoweredBy from "hide-powered-by";
app.use(hidePoweredBy()) 

// or use helmet (which disabled the X-Powered-By header by default)
import * as helmet from "helmet";
app.use(helmet()) 

// do not disable the hidePoweredBy middleware:
// app.use(helmet({ hidePoweredBy: false})) 

// to activate explicitely the hidePoweredBy middleware 
app.use(helmet.hidePoweredBy()); 

app.listen(3000);

