remediationSample:
import * as http from 'http'; 
import * as curl from 'node-curl'; 

http.createServer(function(request,response){ 

    var url = 'https://url'; 
    url += request.url; 

    curl(url, 
        function(err){ 
            response.end(this.body); 
        }) 

}).listen(8000);

sample:
import * as http from 'http'; 
import * as curl from 'node-curl'; 

http.createServer(function(request,response){ 

    var url = 'https://url'; 
    url += request.url; 

    curl(url, 
        { 
            SSL_VERIFYPEER : 0 
        }, 
        function(err){ 
            response.end(this.body); 
        }) 

}).listen(8000);
