using System;
using Microsoft.Data.SqlClient;

// The argument userId should be hard-coded or checked using a specific
// validation method if given by a SQL query
private static void OutputPassword(int userId) //1. OK
{
    using var connection = new SqlConnection(GetConnectionString());
    connection.Open();
    var query = "SELECT * FROM users WHERE id = @id";
    using var command = new SqlCommand(query, connection);
    command.Parameters.AddWithValue("@id", userId); // 2. OK
    using var reader = command.ExecuteReader(); // 3. OK
    while (reader.Read())
    {
        Console.WriteLine("Password=" + reader["password"]);
    }
}

