using System;
using System.Threading;
public class Klass
{
    private static int func(Thread thread)
        {
            bool suspended = false;
            try
            {
                thread.Suspend();
                suspended = true;
                return 1;
            }
            catch (ThreadStateException)
            {
                return 0; //we missed this one
            }
            finally
            {
                if (suspended)
                {
                    thread.Resume();
                }
            }
        }
}
