remediationSample:
// As of Spring Security 4.0, CSRF protection is enabled by default with XML configuration.
// If you would like to disable CSRF protection, the corresponding XML configuration can be seen below.

<http>
    ...
    <csrf disabled="false"/>

// or
    <csrf/>

</http>

// CSRF protection is enabled by default in the Java configuration.

@EnableWebSecurity
public class WebSecurityConfig extends WebSecurityConfigurerAdapter {
        @Override
        protected void configure(HttpSecurity http) throws Exception {
            // No violation CSRF Protection is enabled by default
       }
}

sample:
// As of Spring Security 4.0, CSRF protection is enabled by default with XML configuration.
// If you would like to disable CSRF protection, the corresponding XML configuration can be seen below.

<http>
    ...
    <csrf disabled="true"/>
</http>

// CSRF protection is enabled by default in the Java configuration.

@EnableWebSecurity
public class WebSecurityConfig extends WebSecurityConfigurerAdapter {
        @Override
        protected void configure(HttpSecurity http) throws Exception {
              http
              .csrf().disable();     // Violation CSRF Protection is disabled
       }
}
