protected void configure(HttpSecurity http) throws Exception {
    http
         .authorizeRequests()
                  .anyRequest().authenticated()
                   .and()
         .formLogin()    //Login form is declared after authentication and authorization
                  .loginPage("/login")
                  .permitAll();
}

