#define MAX_ARRAY_SIZE 1000000
int *array = NULL; 
void allocateArray()
{
      int len, itr;
      scanf("%i", &len);
     if (len > 0 && len < MAX_ARRAY_SIZE)
     {
           array = malloc(len);
           for(itr=0;itr<len;itr++)
               array[itr] = 0;
     }
 }

int getValue(int index)
{
    if(index >= 0 && index < MAX_ARRAY_SIZE)
          return array[index];
    else
          return array[MAX_ARRAY_SIZE-1];
}

