sample:
class A
{
// Implementation
};
void fn ( int16_t i )
{
static A a1;
A * a2 = new A;
if ( i > 10 )
{
throw ( &a1 ); // Non-compliant – pointer type thrown
}
else
{
throw ( a2 ); // Non-compliant – pointer type thrown
}
}
