remediationSample:
1. #include <cstddef>

void insert_in_table(int *table, std::size_t tableSize, 
std::size_t pos, // compliant - unsigned pos
int value) {
  if (pos >= tableSize) {
    // Handle error
    return;
  }
  table[pos] = value;
}


2. 
#include <vector>

void insert_in_table(std::vector<int> &table, std::size_t pos, int value) {
  if (pos >= table.size()) {
    // Handle error
    return;
  }
  table[pos] = value;
}

