#include <algorithm>
#include <vector>
void f(const std::vector<int> &src) {

  // Initialize dest with src.size() or construct dest from src directly

  std::vector<int> dest(src.size()); OR  std::vector<int> dest(src);
  std::copy(src.begin(), src.end(), dest.begin());
  // ...
}

OR


#include <algorithm>
#include <iterator>
#include <vector>

void f(const std::vector<int> &src) {
  std::vector<int> dest;

//This iterator expands the destination container by one element for each element supplied by the algorithm
  std::copy(src.begin(), src.end(), std::back_inserter(dest));
  // ...
}


2. 
//Sufficient Initial Capacity
#include <algorithm>
#include <vector>

void f() {
  std::vector<int> v(10);
  std::fill_n(v.begin(), 10, 0x42);
}

OR
//Full Initialisation
#include <algorithm>
#include <vector>

void f() {
  std::vector<int> v(10, 0x42);
}

