remediationSample:
CLOB Variables in PL/SQL
declare
   myStory VARCHAR2(32000);
   revisedStory VARCHAR2(32000);
   myGist VARCHAR2(100);
   revisedGist VARCHAR2(100);
 begin
  -- select a VARCHAR2(32000) column into a VARCHAR2(32000) variable
  SELECT Story INTO myStory FROM print_media WHERE product_id=10;
  -- perform VARCHAR2 operations on a VARCHAR2(32000) variable
  revisedStory := UPPER(SUBSTR(myStory, 100, 1)); 
  -- revisedStory is a VARCHAR2(32000)
  -- Concat a VARCHAR2 at the end of a VARCHAR2(32000)
  revisedStory := revisedStory || myGist;

  end;


create table tab_dbms_lob_search(
   clob_id number,
   c VARCHAR2(32000)

);

sample:
CLOB Variables in PL/SQL
declare
   myStory CLOB;
   revisedStory CLOB;
   myGist VARCHAR2(100);
   revisedGist VARCHAR2(100);
 begin
  -- select a CLOB column into a CLOB variable
  SELECT Story INTO myStory FROM print_media WHERE product_id=10;
  -- perform VARCHAR2 operations on a CLOB variable
  revisedStory := UPPER(SUBSTR(myStory, 100, 1)); 
  -- revisedStory is a temporary LOB
  -- Concat a VARCHAR2 at the end of a CLOB
  revisedStory := revisedStory || myGist;

  end;


create table tab_dbms_lob_search(
   clob_id number,
   c clob

);
