// ----------------------------------------------------------------------------
// 1) Java sample

import jakarta.servlet.annotation.WebServlet;
import jakarta.servlet.http.HttpServlet;
import jakarta.servlet.http.HttpServletRequest;
import jakarta.servlet.http.HttpServletResponse;
import org.owasp.encoder.Encode;

@WebServlet("/profile")
public class ProfileServlet_secure extends HttpServlet {

  @Override
  protected void doGet(HttpServletRequest request, HttpServletResponse response) {

    String userAgent = request.getParameter("customUserAgent");
    String redirectUrl = request.getParameter("redirect");

    // ✓ OK, using sanitization
    response.addHeader("X-Custom-User-Agent", Encode.forJava(userAgent));
    response.addHeader("Location", Encode.forJava(redirectUrl));
  }
}


// ----------------------------------------------------------------------------
// 2) C# sample

using System;
using System.Net;
using System.Web;

private static void RunSecure()
{
  HttpListener listener = new HttpListener();
  listener.Prefixes.Add("http://localhost:8080/");
  listener.Start();
  Console.WriteLine("Server started");

  while (true)
  {
    HttpListenerContext context = listener.GetContext();
    string username = context.Request.QueryString["username"];
    string returnUrl = context.Request.QueryString["returnUrl"];

    // ✓ OK, using sanitization
    context.Response.AddHeader("X-Username", HttpUtility.UrlEncode(username));
    context.Response.AddHeader("X-Return-URL", HttpUtility.UrlEncode(returnUrl));

    byte[] buffer = System.Text.Encoding.UTF8.GetBytes("Headers added");
    context.Response.OutputStream.Write(buffer, 0, buffer.Length);
    context.Response.Close();
  }
}

