// ----------------------------------------------------------------------------
// 1) Java sample

public int getUserId_secure(String userName) throws SQLException {
  String sql = "SELECT user_id FROM users WHERE name = ?";

  try (Connection conn = getConnection("jdbc:mysql://localhost/mydb");
    PreparedStatement pstmt = conn.prepareStatement(sql)) {

    // Parameter binding ensures user input is treated as data, not code
    pstmt.setString(1, userName);

    try (ResultSet rs = pstmt.executeQuery()) { ✓ OK
      return rs.next() ? rs.getInt("user_id") : 0;
    }
  }
}

// ----------------------------------------------------------------------------
// 2) C# sample

private static int GetUserId_secure(string userName)
{
  string sql = "SELECT [UserID] FROM [Users] WHERE [Name] = @userName";

  using (var connection = new SqlConnection("connectionString"))
  using (var command = new SqlCommand(sql, connection))
  {
    // Parameter binding ensures user input is treated as data, not code
    command.Parameters.AddWithValue("@userName", userName);

    connection.Open();
    using (var reader = command.ExecuteReader()) // ✓ OK
    {
      return reader.Read() ? reader.GetInt32(0) : 0;
    }
  }
}

