import javax.naming.Context;
import javax.naming.NamingEnumeration;
import javax.naming.NamingException;
import javax.naming.directory.*;
import java.util.*;
import org.owasp.esapi.Encoder;
import org.owasp.esapi.reference.DefaultEncoder;


public class LdapUserService {
  private DirContext context;
  private SearchControls searchControls;

  public LdapUserService() throws NamingException {
    // Initialize LDAP context
    // ../..
  }

  // Secure: input validation and LDAP encoding
  public List<User> searchUsers_secure(String username, String department) throws NamingException {
    Encoder encoder = DefaultEncoder.getInstance();
    // LDAP encoding for special characters (RFC 4515 compliant)
    String safeUsername = encoder.encodeForLDAP(username);
    String safeDepartment = encoder.encodeForLDAP(department);

    String searchFilter = "(&(uid=" + safeUsername + ")(department=" + safeDepartment + "))";

    // ✓ OK
    NamingEnumeration<SearchResult> results = context.search(
        "ou=users,dc=company,dc=com",
        searchFilter,
        searchControls
    );
    return processResults(results);
  }
}

public class Main {
  public static void main(String[] args) throws NamingException {
    String username = args[0];
    String department = args[1];
    var ldapUserService = new LdapUserService();
    System.out.println(ldapUserService.searchUsers_secure(username, department).getFirst());
  }
}

