using System;
using System.DirectoryServices;
using System.Linq;
using Microsoft.Security.Application;

internal class Program
{
  // Secure: input validation and LDAP encoding with AntiXSS
  public static DirectoryEntry[] FindUsers_secure(string firstName, string lastName)
  {
    // LDAP encoding using AntiXSS library (RFC 4515 compliant)
    string safeFirstName = Encoder.LdapFilterEncode(firstName);
    string safeLastName = Encoder.LdapFilterEncode(lastName);

    string filter = $"(&(givenName={safeFirstName})(sn={safeLastName}))";

    // Connect to Active Directory
    using DirectoryEntry directoryEntry = new DirectoryEntry("LDAP://dc=company,dc=com");
    DirectorySearcher searcher = new DirectorySearcher(directoryEntry);
    searcher.Filter = filter; // ✓ OK
    return searcher.FindAll().Cast<SearchResult>().Select(r => r.GetDirectoryEntry()).ToArray();
  }

  static void Main(string[] args)
  {
    var firstName = args[0];
    var lastName = args[1];
    Console.WriteLine(FindUsers_secure(firstName, lastName));
  }
}

