// ----------------------------------------------------------------------------
// 1) Java sample

public class FileProcessor {
  public static String processFile(String userInput) {
    try {
      // User input directly concatenated into command
      Process process = Runtime.getRuntime().exec("grep 'pattern' " + userInput); // ⚠️ Vulnerability: possible injection, the variable userInput is tainted
      // Process output...
      return "File processed";
    } catch (Exception e) {
      return "Error processing file";
    }
  }
}

// ----------------------------------------------------------------------------
// 2) C# sample

public static class SystemUtility
{
  public static string ExecuteCommand(string userInput)
  {
    ProcessStartInfo startInfo = new ProcessStartInfo();
    startInfo.FileName = "cmd.exe";
    // Dangerous: user input directly embedded
    startInfo.Arguments = "/C dir " + userInput;

    Process process = Process.Start(startInfo); // ⚠️ Vulnerability: possible injection, the variable startInfo is tainted
    // Process results...
    return "Command executed";
  }
}
