------- C# ---------

// with using statement:
public string ReadTextFile(string path)
{
   using (var stream = File.Open(path, FileMode.Open))
   {
      using (var reader = new StreamReader(stream))
      {
        return reader.ReadToEnd();
      }
   }
}

