remediationSample:
[Serializable]
public class MyObject
{
    public int n1 = 0;
    public int n2 = 0;
    public String str = null;

    public override int GetHashCode()
    {
        return n1;
    }

    public override bool Equals(Object obj)
    {
        if (obj == null || !(obj is MyObject))
            return false;
        else
            return n1 == ((MyObject)obj).n1;
    }

}

