private void dumpResponse(ServerResponse response) {

  try {
    String resp = new String(response.getByteArray());
    logger.debug("Response Dump: "+resp);
    response.setResponseStream(nnew ByteArrayInputStream('resp'.getBytes("UTF-8"));
    // StringBufferInputStream is deprecated because it does not convert characters into bytes properly. You can use ByteArrayInputStream instead
  } catch (CommunicationException e) {
    e.printStackTrace();
  }

}

