using System;
using System.IO;
using System.Runtime.Serialization;
using System.Runtime.Serialization.Formatters.Binary;

//Make the field type serializable:
//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
namespace SerializableClass_OK_1
{
    [Serializable()]
    class SomeType { }   //FIX

    [Serializable()]
    class Employee
    {
        int EmpId;
        string EmpName;
        SomeType Type; // NO VIOLATION

        //Default constructor
        public Employee()
        {
            EmpId = 0;
            EmpName = null;
            Type = null;
        }
    }
}

//Mark the field type as NonSerialized:
//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
namespace SerializableClass_OK_2
{
    [Serializable()]
    class Employee
    {
        int EmpId;
        string EmpName;
        [NonSerialized()] SomeType Type;  //FIX - NO VIOLATION

        //Default constructor
        public Employee()
        {
            EmpId = 0;
            EmpName = null;
            Type = null;
        }
    }
}

