// ----------------------------------------------------------------------------
// 1) Java sample

import jakarta.servlet.ServletException;
import jakarta.servlet.annotation.WebServlet;
import jakarta.servlet.http.HttpServlet;
import jakarta.servlet.http.HttpServletRequest;
import jakarta.servlet.http.HttpServletResponse;
import org.apache.commons.text.StringEscapeUtils;

import java.io.IOException;
import java.io.PrintWriter;

@WebServlet("/search")
public class SearchServlet_secure extends HttpServlet {
  protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
    String query = request.getParameter("q");
    response.setContentType("text/html");
    PrintWriter out = response.getWriter();

    String safeQuery = StringEscapeUtils.escapeHtml4(query);
    // ✓ OK. Secure: HTML encode user input before output
    out.println("<h2>Search results for: " + safeQuery + "</h2>");
  }
}


// ----------------------------------------------------------------------------
// 2) C# sample

public static void Xss_secure()
{
  string userInput = Console.ReadLine();
  // ✓ OK. Encoded using HttpUtility.HtmlEncode
  Console.WriteLine("You entered: " + HttpUtility.HtmlEncode(userInput));
}

