public void XssInjection(string currentUsername)
{
    DbAccess conn = new DbAccess();
    SqlCommand command = new SqlCommand("Select address from Users where User_Name=@user_name", conn.getConnection());
    command.Parameters.AddWithValue("@user_name", currentUsername);
