import org.apache.commons.text.StringEscapeUtils;
import java.sql.*;
import java.util.Scanner;
import static java.sql.DriverManager.getConnection;

public class Main {
  public static String getUserInput(int userId) throws SQLException {
    String sql = "SELECT user_name FROM app_users WHERE user_id = ?";
    try (Connection conn = getConnection("jdbc:mysql://localhost/mydb");
       Statement stmt = conn.createStatement();
       ResultSet rs = stmt.executeQuery(sql)) {
      return rs.next() ? rs.getString(1) : "";
    }
  }

  private static void render_secure(int userId, java.io.PrintWriter out) throws SQLException {
    String userInput = getUserInput(userId);
    String safeUserInput = StringEscapeUtils.escapeHtml4(userInput);
    // ✓ OK. Secure: HTML encode user input before output
    out.println("<h2>Search results for: " + safeUserInput + "</h2>");
    out.println("<p>No results found for '" + safeUserInput + "'</p>");
  }

  public static void main(String[] args) throws Exception {
    Scanner scanner = new Scanner(System.in);
    int userId = scanner.nextInt();
    java.io.PrintWriter out = new java.io.PrintWriter(System.out, true);
    render_secure(userId, out);
  }
}

