using Microsoft.Data.SqlClient;
using System;
using System.Text.Encodings.Web;

internal class Program
{
  // Retrieve stored user content from database (mirrors QR8420 data acquisition)
  private static string GetStoredComment(int commentId)
  {
    string userInput = "";
    string sql = "SELECT [Comment] FROM [Comments] WHERE [CommentId] = @commentId";
    using (SqlConnection connection = new SqlConnection("connectionString"))
    {
      connection.Open();
      using (SqlCommand command = new SqlCommand(sql, connection))
      {
        command.Parameters.AddWithValue("@commentId", commentId);
        using (SqlDataReader reader = command.ExecuteReader())
        {
          if (reader.Read())
          {
            userInput = reader.GetString(0); // Could contain persistent XSS payload
            return userInput;
          }
        }
      }
    }
    return userInput;
  }

  private static string RenderComment_secure(int commentId)
  {
    string rendered = "";
    string userInput = GetStoredComment(commentId);

    // Secure: encode output to prevent persistent XSS
    string safe = HtmlEncoder.Default.Encode(userInput);
    string html = "<div>" + safe + "</div>";
    rendered = html;
    Console.WriteLine(html); // ✓ OK
    return rendered;
  }

  static void Main(string[] args)
  {
    var commentId = int.Parse(Console.ReadLine());
    RenderComment_secure(commentId);
  }
}

