  }
}

public String getUserAddress_secure(int userId) throws SQLException {
  String userName = getUserName(userId);

  // SECURE: Using parameterized query even with database-retrieved data
  String sql = "SELECT user_address FROM app_users WHERE user_name = ?";
  try (PreparedStatement stmt = conn.prepareStatement(sql)) {
  // Parameterized query protects against second-order injection
    stmt.setString(1, userName);
    ResultSet rs = stmt.executeQuery(); // OK
    return rs.next() ? rs.getString(1) : "";
  }
}

