remediationSample:
Avoid user inputs in sensitive methods.
If this is not possible, validate the arguments using a dedicated method and declare this method as a sanitization method for the quality rule. Example of such methods:

// ----------------------------------------------------------------------------
// 1) Java

private static int validateAndLimitDelay(String delayInput) {
  final int MIN_DELAY = 0;
  final int MAX_DELAY = 5000; // 5 seconds maximum
  final int DEFAULT_DELAY = 1000; // 1 second default

  if (delayInput == null || delayInput.trim().isEmpty()) {
    System.out.println("Input is empty. Using default " + DEFAULT_DELAY + " ms.");
    return DEFAULT_DELAY;
  }
  int requestedDelay;
  try {
    requestedDelay = Integer.parseInt(delayInput.trim());
  } catch (NumberFormatException e) {
    System.out.println("Invalid integer '" + delayInput + "'. Using default " + DEFAULT_DELAY + " ms.");
    return DEFAULT_DELAY;
  }
  if (requestedDelay == 0) {
    System.out.println("Delay is zero. Using default " + DEFAULT_DELAY + " ms.");
    return DEFAULT_DELAY;
  }
  if (requestedDelay < MIN_DELAY || requestedDelay > MAX_DELAY) {
    int clamped = Math.max(MIN_DELAY, Math.min(requestedDelay, MAX_DELAY));
    System.out.println("Delay " + requestedDelay + " ms outside range [" + MIN_DELAY + "," + MAX_DELAY + "]. Clamping to " + clamped + " ms.");
    return clamped;
  }
  return requestedDelay;
}


// ----------------------------------------------------------------------------
// 2) C# 

// Parses the input string, ensures it's an integer and clamps it between
// the method-local Min/Max. Returns a safe int to use with Thread.Sleep.
private static int ParseAndClampTimeout(string timeoutInput)
{
  const int MinTimeoutMs = 0;
  const int MaxTimeoutMs = 60000; // 60 seconds
  const int DefaultTimeoutMs = 1000; // 1 second

  if (string.IsNullOrWhiteSpace(timeoutInput))
  {
    Console.WriteLine($"Input is empty. Using default {DefaultTimeoutMs} ms.");
    return DefaultTimeoutMs;
  }

  if (!int.TryParse(timeoutInput, NumberStyles.Integer, CultureInfo.InvariantCulture, out int parsed))
  {
    Console.WriteLine($"Invalid integer '{timeoutInput}'. Using default {DefaultTimeoutMs} ms.");
    return DefaultTimeoutMs;
  }

  if (parsed < MinTimeoutMs || parsed > MaxTimeoutMs)
  {
    int clamped = Math.Clamp(parsed, MinTimeoutMs, MaxTimeoutMs);
    Console.WriteLine($"Timeout {parsed} ms outside range [{MinTimeoutMs},{MaxTimeoutMs}]. Clamping to {clamped} ms.");
    return clamped;
  }

  return parsed;
}

