public class Main {
  // Vulnerable: user controls thread sleep duration directly
  private static void processWithDelay_unsecure(String delayInput) {
    try {
      int delay = Integer.parseInt(delayInput);
      System.out.println("Processing request...");
      Thread.sleep(delay); // ⚠️ Vulnerability: user controls sleep time
      System.out.println("Request processed successfully");
    } catch (Exception e) {
      System.err.println("Error: " + e.getMessage());
    }
  }

  public static void main(String[] args) {
    if (args.length > 0) {
      processWithDelay_unsecure(args[0]);
    }
   }
}


