using System;
using System.Threading;

public class TaskManager
{
  public static void Main(string[] args)
  {
    if (args.Length > 0)
    {
      var timeoutInput = args[0];
      ExecuteWithTimeout_unsecure(timeoutInput);
    }
  }

  // Vulnerable: user controls thread sleep duration directly
  // Attack scenarios:
  // - Input: `86400000` → Forces thread to sleep for 24 hours
  // - Input: `2147483647` → Maximum int value(~25 days sleep)
  // - Input: `-1` → Potential infinite sleep or exception
  // - Multiple concurrent requests with high values → Complete service DoS
  private static void ExecuteWithTimeout_unsecure(string timeoutInput)
  {
    try
    {
      int timeout = int.Parse(timeoutInput);
      Console.WriteLine("Starting task execution...");
      Thread.Sleep(timeout); // ⚠️ Vulnerability: user controls sleep time
      Console.WriteLine("Task completed successfully");
    }
    catch (Exception ex)
    {
      Console.WriteLine($"Error: {ex.Message}");
    }
  }
}
