import java.util.regex.Pattern;
import javax.script.ScriptEngineManager;
import javax.script.ScriptEngine;

public class Main {
    public static void main(String[] args) {
        if (args.length > 0) {
            evaluation_secure(args[0]);
        }
    }

    // Secure evaluation: strict input validation
    private static void evaluation_secure(String userInput) {
        Integer parsedValue = trySanitizeInteger(userInput);
        if (parsedValue == null) {
            System.out.println("Invalid input: expected a 32-bit integer.");
            return;
        }
        int result = Math.max(10, parsedValue); // ✓ OK
        System.out.println("Result: " + result);
    }

    // Strict validation: numeric whitelist and safe parsing
    private static Integer trySanitizeInteger(String input) {
        if (input == null || input.trim().isEmpty()) return null;
        input = input.trim();
        // Whitelist: only optional +/-, then digits
        if (!Pattern.matches("^[+-]?\\d+$", input)) return null;
        try {
            return Integer.parseInt(input);
        } catch (NumberFormatException e) {
            return null;
        }
    }
}


