// 2) C# sample

using Microsoft.CodeAnalysis.CSharp.Scripting;
using System;
using System.Text.RegularExpressions;
using System.Threading.Tasks;

internal class Program
{
    static async Task Main(string[] args)
    {
        if (args.Length > 0)
            await Evaluation_secure(args[0]);
    }

    // Secure evaluation that delegates sanitization to a single-purpose method
    private static Task Evaluation_secure(string userInput)
    {
        if (!TrySanitizeInteger(userInput, out var parsedValue))
        {
            Console.WriteLine("Invalid input: expected a 32-bit integer.");
            return Task.CompletedTask;
        }

        var result = Math.Max(10, parsedValue); // ✓ OK
        Console.WriteLine($"Result: {result}");
        return Task.CompletedTask;
    }

    // Method dedicated to securing user input: strict whitelist + safe parse
    private static bool TrySanitizeInteger(string input, out int value)
    {
        value = 0;

        if (string.IsNullOrWhiteSpace(input))
            return false;

        input = input.Trim();

        // Whitelist: only optional leading +/-, then digits
        if (!Regex.IsMatch(input, @"^[+-]?\d+$"))
            return false;

        // Safe numeric parse (protects against overflow and non-numeric content)
        return int.TryParse(input, out value);
    }
}

