import jakarta.servlet.http.HttpServletResponse;
import org.owasp.esapi.ESAPI;
import org.springframework.http.ResponseEntity;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.RequestParam;
import org.springframework.web.bind.annotation.RestController;

@RestController
public class UserControllerSecure {

  @GetMapping("/api/user/profile")
  public ResponseEntity<String> getUserProfile(@RequestParam String userRole,
                         @RequestParam String redirectUrl,
                         HttpServletResponse response) {

    // ✓ OK: using ESAPI sanitization before header insertion
    response.addHeader("X-User-Role", ESAPI.encoder().encodeForHTML(userRole));
    response.addHeader("Location", ESAPI.encoder().encodeForHTML(redirectUrl));

    return ResponseEntity.ok("Profile loaded");
  }
}



