// 2) C# sample

using Confluent.Kafka;
using System.Net;
using System.Web;

internal class Program
{
  private static Dictionary<string, string> config;

  private void ProcessHeaderUpdateFromKafka_secure()
  {
    using var consumer = new ConsumerBuilder<Ignore, string>(config).Build();
    consumer.Subscribe("header-update-topic");
    var result = consumer.Consume(TimeSpan.FromSeconds(10));
    if (result is null)
      return;

    string headerValue = result.Message.Value;
    string redirectLocation = result.Message.Headers.GetLastBytes("redirect")?.ToString();

    HttpListener listener = new HttpListener();
    listener.Prefixes.Add("http://localhost:8080/");
    listener.Start();

    HttpListenerContext context = listener.GetContext();

    // ✓ OK: proper sanitization before header insertion
    context.Response.AddHeader("X-Custom-Header", HttpUtility.UrlEncode(headerValue));
    context.Response.AddHeader("Location", HttpUtility.UrlEncode(redirectLocation));

    context.Response.Close();
  }

  static void Main(string[] args)
  {
    Console.WriteLine("Hello, World!");
  }
}

