class Base 
{ 
   public: 
       virtual void foo( short ); 
       virtual void foo( double ); 
}; 
class Derived : public Base 
{ 
   public: 
       virtual void foo( short );  // => VIOLATION foo(double) not redefined!
       void bar() 
       { 
         foo( 0.1 ); // calls Derived::foo( short )! 
       } 
};
